import React, { useEffect, useState } from 'react';

interface Event {
  id: string;
  title: string;
  description: string;
  startTime: string;
  endTime: string;
}

const EventsPage: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [expandedId, setExpandedId] = useState<string | null>(null);

  useEffect(() => {
    const stored = localStorage.getItem('events');
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        const eventsArray: Event[] = Array.isArray(parsed) ? parsed : parsed.events || [];
        // Filter out events that ended already
        const now = new Date();
        const filtered = eventsArray.filter(event => new Date(event.endTime) > now);
        setEvents(filtered);
      } catch {
        setEvents([]);
      }
    }
  }, []);

  const toggleExpand = (id: string) => {
    setExpandedId(prev => (prev === id ? null : id));
  };

  return (
    <div className="events-page">
      <h1>Upcoming Events</h1>
      {events.length === 0 ? (
        <p>No upcoming events.</p>
      ) : (
        <div className="event-cards">
          {events.map(event => (
            <div
              key={event.id}
              className={`event-card ${expandedId === event.id ? 'expanded' : ''}`}
              onClick={() => toggleExpand(event.id)}
              style={{
                cursor: 'pointer',
                border: '1px solid #ccc',
                borderRadius: 8,
                padding: 16,
                width: 300,
                marginBottom: 16,
                backgroundColor: expandedId === event.id ? '#f5f5f5' : 'white',
                color: expandedId === event.id ? '#000' : '#333',
                transition: 'background-color 0.3s ease, color 0.3s ease',
                wordWrap: 'break-word', // ensures description and text wrap nicely
              }}
            >
              <h2>{event.title}</h2>
              <p>
                {new Date(event.startTime).toLocaleString()} â€“ {new Date(event.endTime).toLocaleString()}
              </p>
              {expandedId === event.id && <p>{event.description}</p>}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EventsPage;
