import React, { useEffect, useState } from 'react';
import '../styling/style.sass'; // Import your SASS file

interface Event {
  id: string;
  title: string;
  description: string;
  startTime: string;
  endTime: string;
}

const EventsPage: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [expandedId, setExpandedId] = useState<string | null>(null);

  useEffect(() => {
    const now = new Date();
    const storedEvents = localStorage.getItem('events');

    if (storedEvents) {
      const parsed = JSON.parse(storedEvents);
      // Expecting { events: Event[] }
      const eventsArray: Event[] = parsed.events || [];

      // Filter to only events that haven't ended yet
      const filteredEvents = eventsArray.filter(
        event => new Date(event.endTime) > now
      );

      setEvents(filteredEvents);
    }
  }, []);

  const toggleExpand = (id: string) => {
    setExpandedId(prev => (prev === id ? null : id));
  };

  return (
    <div className="events-page">
      <h1>Upcoming Events</h1>
      {events.length === 0 ? (
        <p>No upcoming events.</p>
      ) : (
        <div className="event-cards">
          {events.map(event => {
            const isExpanded = expandedId === event.id;

            return (
              <div
                key={event.id}
                onClick={() => toggleExpand(event.id)}
                className={`event-card ${isExpanded ? 'expanded' : ''}`}
              >
                <h2>{event.title}</h2>
                <p>
                  {new Date(event.startTime).toLocaleString()} â€“{' '}
                  {new Date(event.endTime).toLocaleString()}
                </p>
                <div className={`event-description ${isExpanded ? 'show' : ''}`}>
                  <strong>Description:</strong>
                  <p>{event.description}</p>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default EventsPage;
