import React, { useEffect, useState } from 'react';
import '../styling/style.sass';

interface Event {
  id: string;
  title: string;
  description: string;
  startTime: string;
  endTime: string;
}

const EventsPage: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  // Change expandedId to a Set of expanded IDs
  const [expandedIds, setExpandedIds] = useState<Set<string>>(new Set());

  useEffect(() => {
    const now = new Date();
    const storedEvents = localStorage.getItem('events');

    if (storedEvents) {
      const parsed = JSON.parse(storedEvents);
      const eventsArray: Event[] = parsed.events || [];

      const filteredEvents = eventsArray.filter(
        event => new Date(event.endTime) > now
      );
      setEvents(filteredEvents);
    }
  }, []);

  const toggleExpand = (id: string) => {
    setExpandedIds(prev => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  console.log('Expanded IDs:', Array.from(expandedIds));

  return (
    <div className="events-page">
      <h1>Upcoming Events</h1>
      {events.length === 0 ? (
        <p>No upcoming events.</p>
      ) : (
        <div className="event-cards">
          {events.map(event => (
            <div
              key={event.id}
              onClick={() => toggleExpand(event.id)}
              className={`event-card ${expandedIds.has(event.id) ? 'expanded' : ''}`}
            >
              <h2>{event.title}</h2>
              <p>
                {new Date(event.startTime).toLocaleString()} â€“{' '}
                {new Date(event.endTime).toLocaleString()}
              </p>
              {expandedIds.has(event.id) && (
                <div className="event-description">
                  <strong>Description:</strong>
                  <p>{event.description}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EventsPage;
